<h2>Products</h2>
<div class="content">
  <div *ngFor="let product of products" class="item-card">
    <img [src]="product.image" class="phone" draggable="false" />
    <h3>
      <a
        [title]="product.name + ' details'"
        [routerLink]="['/products', product.id]"
        >{{ product.name }}</a
      >
    </h3>
    <p *ngIf="product.description">Description: {{ product.description }}</p>
    <button (click)="share(product.name)">Share</button>

    <app-product-alerts
      [product]="product"
      (notify)="onNotify()"
    ></app-product-alerts>
  </div>
</div>

